import React,{useEffect, useState} from 'react'
import CloudPie from './Pie'
import request from '../../utils/request'
import Moment from 'moment'

function Grid({pieCloud, pieCloudCount}) {
  let [state,setState] = useState([])

  useEffect(()=>{
    request('GET','/auth/getUser', {
        params: {
          limit: 5, pgno: 1
        }
      }).then(res=>{
        if(res.data.success){
        setState(res.data.user)
      }
    })
  }, [])
    return (
        <>
        <div className="col-xl-6 col-md-12 mt-3">
          <div className="card card-body border-0">
            <h6 className="card-title mb-3">Most Recent Plans</h6>
            <div className="table-responsive" tabIndex={1} style={{overflow: 'hidden', outline: 'none'}}>
              <table className="table table-bordered ">
                <thead>
                  <tr>
                    <th>Plan</th>
                    <th>User</th>
                    <th>Country</th>
                    <th>Created Date</th>
                  </tr>
                </thead>
                <tbody>
                  {state.map((user, i) =>{
                      return (<tr key={i}>
                        <td>{user.org_name}</td>
                        <td>{user.f_name + ' ' + user.l_name}</td>
                        <td>
                          <span className="logo-icon" tooltip={user.country}>
                            <img src={`https://www.countryflags.io/${user.sortname}/flat/64.png`} className="img-responsive ml-1" alt={'Flag of '+ user.country}></img>
                          </span>
                        </td>
                        <td>{Moment(user.creation_date).format('DD-MM-YYYY')}</td>
                      </tr>)
                    }
                  )}
                </tbody>
              </table>
            </div>
          </div>
          <div className="card card-body mt-2">
            <div className="card border-0 col-md-12">
              <CloudPie pieCloud={pieCloud} pieCloudCount={pieCloudCount} />
            </div>
          </div>
        </div>
        </>
    )
}
export default Grid
