			},
    datasets: [{
      data: pieCloudCount,
      backgroundColor: ["rgb(255, 69, 96)","rgb(254, 176, 25)","#b6b6b6", "rgb(0, 227, 150)", "#8064A1","rgb(254, 176, 25)"],
      indexLabelFontSize: 10
    }],
    labels:pieCloud,
    dataLabels: {
      enabled: true,
      format: '60 %',
      distance: -50,
    },

  }
  let options = {
                chart: {
                  type: 'pie',
                },
                responsive: [{
                  breakpoint: 480,
                  options: {
                    chart: {
                      width: 50
                    },
                    legend: {
                      position: 'bottom'
                    }
                  }
                }]
              }
return(
  <>
  <div className="graph-detail">
  <h5>Number of User per Cloud</h5>
      <div className="graph__01 max-height ">
        <div className="cover col-md-12">
          <Pie title="The Population of Continents and Regions" options={options} data={diskHealthData}/>
    </div>
    </div>
  </div>  </>
)
}

export default CloudPie
