import {createReducer} from 'utils/reducerUtils'

const initialState = {}

function pendingRequest(state, {id, details}){
  return {
    ...state,
    [id]: details
  }
}

function completedRequest(state, {id}){
  let newState = {...state}
  delete newState[id]
  return newState
}

export default createReducer(initialState, {
  'PENDING_REQUEST': pendingRequest,
  'COMPLETED_REQUEST': completedRequest
})
