import React, { useState, useEffect } from 'react'
import request from '../../utils/request';
import Header from '../header/Header';
import Search from './search'
import ListFilter from './filter'
import List from './news'


function Dashboard() {
 let[filterData, setFilterData] = useState([])
 let [newsData, setNewsData] = useState([])
 let [parameters, setParameters] = useState('')
  useEffect(() =>{
    request('GET', '/getAgencyData',{agency_id:1}).then((respNews)=>{
       if(respNews.data.success){
         console.log(">>>>>>>>>>>respnews",respNews)
          setNewsData(respNews.data.news)
       }
    })
    request('GET', '/getNewsData',{category_id:parameters,agency_id:1}).then((respNews)=>{
       if(respNews.data.success){
         console.log(">>>>>>>>>>>respnews",respNews)
          setNewsData(respNews.data.news)
       }
    })
    request('GET', '/getFilterData').then((resp)=>{
       if(resp.data.success){
         console.log(">>>>>>>>>>>",resp)
          setFilterData(resp.data.filters)
       }
    })
  },[setParameters])
  console.log('>>>filterData', filterData)
  return(
   <div className="container">
      <div className="col-md-12">
         <div className="row">
            <div className="col-md-6 market-left">
               <h2>Explore, deploy and manage your blockchain solutions in just a few steps.</h2>
               <div className="market-form">
                  <Search filterData={filterData}/>
                 </div>
            </div>
            <div className="col-md-6 text-right pt-4 market-left-deploy"><img src="/static/images/market-place.svg" /><img src="/static/images/market-place-dark.svg" /></div>
         </div>
         <div className="row mt-4">
            <ListFilter filterData={filterData} parameters={parameters} setParameters={setParameters}/>
            <List setNewsData={setNewsData} newsData={newsData}/>
         </div>
      </div>
   </div>)
}

export default Dashboard
