import axios from 'axios'
import config from '../config/config'
import {toast} from 'react-toastify';
import { logout } from './checkToken';

axios.defaults.headers.common['Content-Type'] = 'application/json'


/*Parameters
* method: get post delete
* path: eg /getUserDetails
* config: check axios documentation
*/

export default function request(method, path, options = {}) {
  console.log(">>>>", path)
  return axios({
    method,
    url: config.webhost + config.webport + path,
    responseType: 'json',
    ...options,
  }).then((res) => {
    if(res.data.success) {
      return res;
    } else if(!res.data.tokenSuccess) {
      logout();
      toast.error(res.data.message);
      window.location = "/";
    } else {
      return;
    }
  }).catch((e) => {
    if (e.response && e.response.status === 401){
      window.location = '/';
      toast.error(e.response);
    } else {
      return e
    }
  })
}
