import React, { useState, useEffect } from 'react'
import request from '../../utils/request';
import Header from '../header/Header';
import Search from './search'
import ListFilter from './filter'
import List from './news'


function Dashboard() {
 let[filterData, setFilterData] = useState([])
 let [newsData, setNewsData] = useState([])
 let [parameters, setParameters] = useState('')
 let [agencyData, setAgencyData] = useState({})
 let [searchData , setSearchData] = useState('')



  useEffect(() =>{
    console.log("setParameters", parameters)
    request('GET', '/getAgencyData',{agency_id:1}).then((respAgency)=>{
       if(respAgency.data.success){
          setAgencyData(respAgency.data.agency)
       }
    })
    request('GET', `/getNewsData?category_id=${parameters}&agency_id=${1}&search=${searchData}`).then((respNews)=>{
       if(respNews.data.success){
          setNewsData(respNews.data.news)
       }
    })
    request('GET', '/getFilterData').then((resp)=>{
       if(resp.data.success){
          setFilterData(resp.data.filters)
       }
    })
  },[parameters, searchData])
  return(
   <div className="container">
      <div className="col-md-12">
      <div className="cover">
   <div className="inline-div ">
      <div className="col-md-1">
         <div className="title-box">
            <h2><i className="fa fa-filter" /> Filter </h2>
         </div>
      </div>
      <div className="col-md-11 inline-div">
         <div className="col-md-3 sh-key-input">
            <input className="form__control " type="text" id="in-value" placeholder="Api Key" defaultValue />
            <ul className="search-key-name">
               <li><span className="key-name mr-auto">Key Name</span><span className="key-value">Key </span></li>
            </ul>
         </div>
         <div className="col-md-2 fixes-button-bottom p-0"><button className="btn blue-btn network-cloud-next">Submit</button></div>
      </div>
   </div>
</div>
         <div className="row">
         <div className="section-filter">
     </div>
            <div className="col-md-6 market-left">
               <h2><b>THE TIMES OF INDIA</b></h2>
               <div className="market-form">
                  <Search  searchData={searchData} setSearchData={setSearchData}/>
                 </div>
            </div>
            <div className="col-md-6 text-right pt-4 market-left-deploy"><img src="/static/images/times.jpg" /><img src="/static/images/market-place-dark.svg" /></div>
         </div>
         <div className="row mt-4">
            <ListFilter filterData={filterData} parameters={parameters} setParameters={setParameters}/>
            <List setNewsData={setNewsData} newsData={newsData}/>
         </div>
      </div>
   </div>)
}

export default Dashboard
