import React from 'react'
import { Doughnut } from 'react-chartjs-2';
import { dlt } from '../../utils/dlt'

function CloudDoughnut({doughNutDlt,doughNutNetworkCount}){
  console.log('doughNutDlt >>>>>>>>>>>>>>>', doughNutDlt)
  console.log('doughNutDlt >>>>>>>>>>>>>>>', doughNutNetworkCount)
  let diskHealthData = {
    title: {
				text: "Number of network per dlt"
			},
    datasets: [{
      data: doughNutNetworkCount,
      backgroundColor: ["#9BBB58","#23BFAA","#ffe0bd", "#C0504E", "#8064A1","#4F81BC"],
      indexLabelFontSize: 10
    }],
    labels:doughNutDlt,
    dataLabels: {
      enabled: true,
      format: '60 %',
      distance: -50,
    },

  }
  let options = {
                chart: {
                  type: 'donut',
                },
                responsive: [{
                  breakpoint: 480,
                  options: {
                    chart: {
                      width: 50
                    },
                    legend: {
                      position: 'bottom'
                    }
                  }
                }]
              }
  console.log('result', diskHealthData)
  return(
    <>
    <div className="graph-detail">
    <h5>Number of Networks per Dlt</h5>
        <div className="graph__01 max-height ">
          <div className="cover col-md-12">
            <Doughnut title="The Population of Continents and Regions" options={options} data={diskHealthData}/>
      </div>
      </div>
    </div>  </>)



}
export default CloudDoughnut
