import * as actionTypes from './ActionTypes';

export function showModal(props) {
    // alert(props)
    return dispatch => {
        if (props == true) {
            dispatch(modalSuccess(true));
        }
        else {
            dispatch(modalFail(false));
        }
    }
}

export function showResetModal(props) {
    // alert(props)
    return dispatch => {
        if (props === true) {
            dispatch(resetModalSuccess(true));
        }
        else {
            dispatch(resetModalFail(false));
        }
    }
}

export function onUserDetatils(props){
    return dispatch => {
        if (props !== null) {
            localStorage.setItem('currentUser',props);
            dispatch(onUserSuccess(props));
        }
        else {
            dispatch(onUserFail());
        }
    }
}

export function onUserSuccess(payload) {
    return {
        type: actionTypes.SET_USER_SUCCESS,
        action: payload
    }
}

export function onUserFail(payload) {
    return {
        type: actionTypes.SET_USER_FAIL,
        action: payload
    }
}


export function modalSuccess(payload) {
    // alert(payload)
    return {
        type: actionTypes.MODAL_OPEN,
        action: payload
    }
}

export function modalFail(payload) {
    return {
        type: actionTypes.MODAL_CLOSE,
        action: payload
    }
}

export function resetModalSuccess(payload) {
    return {
        type: actionTypes.RESET_MODAL_OPEN,
        action: payload
    }
}

export function resetModalFail(payload) {
    return {
        type: actionTypes.RESET_MODAL_CLOSE,
        action: payload
    }
}
