export const isLogin = () => {
    if (localStorage.getItem("auth_token")) {
        return true;
    }

    return false;
}

export const logout = () => {
    localStorage.removeItem('auth_token');
    localStorage.removeItem('currentUser');
    window.location.pathname = "/";
  }
