import moment from 'moment';

export function getGraphData(data) {
    let x_axis_data = [];
    let y_axis_data = [];

    data.map((val, k) => {
        var curr_date = moment(val.creation_date).format('MMMM YYYY');
        x_axis_data.push(curr_date)
        y_axis_data.push(val.user_count)
    });

    return { x_axis_data, y_axis_data };
}


export function getNetworkData(data) {
    let xAxisNetwork = [];
    let yAxisNetwork = [];
    data.map((val, k) => {
        var curr_date = moment(val.creation_time).format('MMMM YYYY');
        xAxisNetwork.push(curr_date)
        yAxisNetwork.push(val.networkCount)
    });

    return { xAxisNetwork, yAxisNetwork };
}
