let components = {
  selectCloud: {
    _id: 'selectCloud',
    name: 'selectCloud',
    isFullScreen: true,
  }
}
export function addOverlayComponent(component, params={},constants={}){
   return {
    type: 'ADD_OVERLAY',
    component: {
      isVisible: true,
      tag: constants,
      ...components[component],
      ...params,
    }
  }
}

export function removeOverlayComponent(id){
  return {
    type: 'REMOVE_OVERLAY',
    id
  }
}

export function showOverlayComponent(id){
  return {
    type: 'SHOW_OVERLAY',
    id
  }
}

export function hideOverlayComponent(id){
  return {
    type: 'HIDE_OVERLAY',
    id
  }
}

export function hideAllOverlayComponent(){
  return {
    type: 'HIDE_ALL_OVERLAY'
  }
}
