import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import request from '../../utils/request';
import {toast} from 'react-toastify';

function Login({history}) {
  let [loginData, setLoginData] = useState({
    email: '',
    password: ''
  })
  let [err_email,  setErr_email] = useState('')
  let [emailValid, setEmailValid] = useState(false);
  let [err_password,  setErr_password] = useState('')
  const handelChange = event => setLoginData({ ...loginData, [event.target.name]: event.target.value })
  const submit = () => {
        let credentials = {
          email: loginData.email,
          password: loginData.password
        }
        request('POST', '/auth/login', {
          data: credentials
        }).then(res => {
          console.log("<<<<<<vvvv", res)
