import React from 'react'

export function makePaginationButtons(total, page, setPgno){
  let paginationButtons = []

	let start = page-1;

  if(total-page<2)
		start = total-3

  if(start<1)
		start = 1

  let end = page+(page===1?1:0)+2

  if(end>total)
		end=total

  if(page!==1)
    paginationButtons.push(<button key='prev' onClick={()=>setPgno(page-1)}>{'<'}</button>)

  for(let i=start; i<=end; i++){
    paginationButtons.push(<button key={i} className={i===page?'active':''} onClick={()=>setPgno(i)}>{i}</button>)
  }

  if(page!==total)
    paginationButtons.push(<button key='end' onClick={()=>setPgno(page+1)}>{'>'}</button>)

  return paginationButtons
}
