import React, { useState, useEffect } from 'react'
import { NavLink, Redirect } from 'react-router-dom'
import { connect } from 'react-redux';
import EditProfileModal from '../modals/EditProfileModal';
import ResetPasswordModal from '../modals/ResetPasswordModal';
import { showModal , showResetModal } from '../../modalStore/modalActions';
import { logout } from '../../utils/checkToken';

function Header({ isModalOpen, showModal ,isResetModalOpen,currentUser,showResetModal,dispatch}) {
  let [currentUserLocal, setCurrentUserLocal] = useState({})

  useEffect(() => {
    setCurrentUserLocal(JSON.parse(localStorage.getItem('currentUser')))
  }, [])

  let user = typeof currentUser == "string" ? JSON.parse(currentUser) : currentUserLocal;

  return (
    <header>
          <a href="index.php" className="navbar-brand"><img src="static/images/logo.png" width={52} /></a>
          <nav className="nav">
            <div className="trigger">
              <ul>
                <li>
                  <NavLink to="/dashboard" className="active" tooltip="Dashboard" flow="down">
                    <i className="fa fa-tachometer"></i>
                  </NavLink>
                </li>
              </ul>
            </div>
            <ResetPasswordModal isResetModalOpen={isResetModalOpen} showResetModal={showResetModal} dispatch={dispatch} />
            <div className="header-nav">
              <ul className="header-nav">
                <li className="profile-details sub-menu-parent">
                  <span className="profile-text">Hello {user.f_name +' '+ user.l_name} <i className="fa fa-angle-down" aria-hidden="true" /></span>
                  <ul className="sub-menu">
                    <li className="Edit-Profile-li" onClick={showModal}><i className="fa fa-user-circle-o text-aqua" /> Edit Profile</li>
                    <li onClick={showResetModal}><i className="fa fa-key text-aqua" /> Reset Password </li>
                    <li>
                      <span onClick={logout} className="active" tooltip="Logout" flow="down">
                        <i className="fa fa-sign-out text-aqua" /> Logout
                      </span>
                    </li>
                  </ul>
                </li>
              </ul>
            </div>
            <EditProfileModal isModalOpen={isModalOpen} showModal={showModal}  />

          </nav>
    </header>)
}

const mapStateToProps = state => {
  return {
    isModalOpen: state.modalReducer.modalState,
    currentUser: state.modalReducer.userDetails,
    isResetModalOpen: state.modalReducer.resetModalState
  }
}
const mapDispatchToProps = dispatch => {
  return {
    showModal: () => dispatch(showModal(true)),
    showResetModal: () => dispatch(showResetModal(true)),
    dispatch

  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Header);
