import React from 'react'
import { Line, Doughnut } from 'react-chartjs-2';

function UserGraph({ y_axis_data, x_axis_data , networkXAxis, networkYAxis }) {
    const state = {
        labels: x_axis_data,
        datasets: [
            {
                label: 'Users',
                fill: false,
                lineTension: 0.5,
                backgroundColor: "rgba(75,192,192,0.2)",
                borderColor: "rgba(75,192,192,1)",
                borderWidth: 2,
                fill: false,
                data: y_axis_data,
            },
            {
                label: 'Networks',
                fill: false,
                lineTension: 0.5,
                borderColor: "#742774",
                borderWidth: 2,
                fill: false,
                data: networkYAxis,
            }
        ]
    }
    console.log('&&&&&&&&&&&&&&&&&', state)
    return (
        <>
            <div className="graph__01">
                <Line data={state} options={{
                    title: {
                        display: true, fontSize: 16
                    },
                    legend: {
                        display: true,
                        position: 'top'
                    },
                    scales: {
                        yAxes: [
                            {
                                scaleLabel: {
                                    display: true,
                                    labelString: 'Number of Users / Networks',
                                    fontSize: 16,
                                    fontColor: "black"
                                }
                            },
                        ],
                        xAxes: [
                            {
                                scaleLabel: {
                                    display: true,
                                    labelString: 'Month',
                                    fontSize: 16,
                                    fontColor: "black"
                                },
                            }
                        ]
                    }
                }} />
            </div>
        </>
    )
}

export default UserGraph
