import React, { useState, useEffect } from 'react'
import request from '../../utils/request';

function Search() {

  async function setSearchData(event){
    let reqData;
    if(type) {
      reqData = {search:event,type:type }
    } else {
      reqData = {search:event,type:type, limit: searchState.limit, pageNo: searchState.pageNo }
    }
    let response = await searchProduct(reqData);
      if(response.success) {
        setMarketProduct(response.getSearchMarketPlaceData)
      }
      if(!type && response.countSearchProduct){
        setSearchState({...searchState, count: response.countSearchProduct})
      }
   }

  return(<>
    <button>
       <img src="/static/images/search.svg" />
    </button>
    <input type="text" onChange={(event)=>setSearchData(event.target.value)} name="search" className="form-control" placeholder="Search" /></>)
}

export default Search
