import * as actionTypes from './ActionTypes';

const initialState = {
    modalState: false,
    userDetails: null,
    resetModalState: false
}

export default function reducer(state = initialState, action) {
    switch (action.type) {
        case actionTypes.MODAL_OPEN:
            return {
                ...state, modalState: action.action,resetModalState:false
            }
        case actionTypes.MODAL_CLOSE:
            return {
                ...state, modalState: action.action
            }
        case actionTypes.RESET_MODAL_OPEN:
            return {
                ...state, resetModalState: action.action,modalState:false
            }
        case actionTypes.RESET_MODAL_CLOSE:
            return {
                ...state, resetModalState: action.action
            }
        case actionTypes.SET_USER_SUCCESS:
            return {
                ...state, userDetails: action.action
            }
        case actionTypes.SET_USER_FAIL:
            return {
                ...state, userDetails: ""
            }
        default:
            return {
                ...state
            }

    }
}
